## -- Define the class tile which is a sprite
class Tile(pygame.sprite.Sprite):
    # Define the constructor for invader
    def __init__(self, color, width, height, x_ref, y_ref):
        # Call the sprite constructor
        super().__init__()
        self.image = pygame.Surface([width, height])
        self.image.fill(color)
        self.rect = self.image.get_rect()
        self.rect.x = x_ref
        self.rect.y = y_ref

# Create a list of all sprites
all_sprites_list = pygame.sprite.Group()
# Create a list of tiles for the walls
wall_list = pygame.sprite.Group()

# Create walls on the screen (each tile is 20 x 20 so alter cords)
def create_boxes():
    for y in range(32):
        for x in range(32):
            if map[x][y] == 1:
                my_wall = Tile(BLUE, 20, 20, x*20, y * 20)
                all_sprites_list.add(my_wall)
            else:
                my_wall = Tile(BLACK, 20, 20, x*20, y * 20)
                all_sprites_list.add(my_wall)
            #end if
        #end for
    #end for
    grid()

def grid():
    # Creating the visual representation of the Grid (20 by 20px - 32 rows and collumns)
    #vertical grid
    for x in range(33):
        all_sprites_list.add(Line(WHITE, [x * 20 - 0.5, 0], 1, 640, 1))
    #horizontal grid
    for y in range(33):
        all_sprites_list.add(Line(BLUE, [0, y * 20 - 0.5], 640, 1, 1))

class Line(pygame.sprite.Sprite):
    # Define the constructor for invader
    def __init__(self, color, start, height, width, thickness):
        # Call the sprite constructor
        super().__init__()
        self.image = pygame.Surface([height, width])
        self.image.fill(color)
        self.rect = self.image.get_rect()
        self.rect.x = start[0]
        self.rect.y = start[1]

grid()
### -- Game Loop
while not done:
    # -- User input and controls
    for event in pygame.event.get():
        if event.type == pygame.QUIT:
            done = True
        if event.type == pygame.MOUSEBUTTONDOWN and event.button == LEFT:
            mouse_pos_x, mouse_pos_y = pygame.mouse.get_pos()
            column = mouse_pos_x // 20
            row = mouse_pos_y // 20
            #print(column, row)
            map[column][row] = 1
            # Create a new file map_export.txt
            file = open("map_export.txt", "w+")
            # Write the map inside of the file
            file.write(str(map))
            # Create walls on the screen (each tile is 20 x 20 so alter cords)
            create_boxes()
        if event.type == pygame.MOUSEBUTTONDOWN and event.button == RIGHT:
            mouse_pos_x, mouse_pos_y = pygame.mouse.get_pos()
            column = mouse_pos_x // 20
            row = mouse_pos_y // 20
            #print(column, row)
            map[column][row] = 0
            # Create a new file map_export.txt
            file = open("map_export.txt", "w+")
            # Write the map inside of the file
            file.write(str(map))
            # Create walls on the screen (each tile is 20 x 20 so alter cords)
            create_boxes()
    #next event
    all_sprites_list.update()
    # -- Screen background is BLACK
    screen.fill(BLACK)
    
    #pygame.draw.line(screen, WHITE, (20, 0), (20, 640), 1)
    all_sprites_list.draw(screen)
    # -- Game logic goes after this comment
    pygame.display.flip()
    # - The clock ticks over
    clock.tick(60)
#End While - End of game loop
pygame.quit()

